import TopMenu from "../TopMenu/TopMenu"
import React, { Component } from "react";
import "./paymentsummary.css"
import axios from "axios";

class Paymentsummary extends Component {
    constructor(props) {
        super(props);
        this.state = {
            intencion:String,
            Payment_id:String,
            amount :Number,
            mass :String,
            massscribe: String,
            textintens:String,
        }

    }
    //ReactDOM.render()
    componentDidMount = async () => {
        var intencion=localStorage.getItem("Intention");
        var Payment_id=localStorage.getItem("Payment_id");
        var amount =localStorage.getItem("Amount");
        var mass =localStorage.getItem("Mass");
        var massscribe= localStorage.getItem("MassScribe");
        var textintens=localStorage.getItem("Textintens");
        if(intencion!=null,Payment_id!=null&&amount!=null&&mass!=null&&massscribe!=null&&textintens!=null){
            this.setState({
                intencion:intencion,
            Payment_id:Payment_id,
            amount :amount,
            mass :mass,
            massscribe: massscribe,
            textintens:textintens
            })
        }
    }
    render() {
        var{intencion,Payment_id,amount,mass,massscribe,textintens}=this.state
        return (
            <>
                <TopMenu />
                <div className="kontent" >
                    <div className="header">Podsumowanie zgaszaniej Intencji</div>
                    <div>Intencja zamawiana na {massscribe.replace("Msza", "mszę")}</div>
                    <div>Treść Twojej intencji: {textintens}</div>
                    <div>Twoja ofiara:{amount}zł.</div>
                </div>
            </>

        )

    }

}
export default Paymentsummary
