import TopMenu from "../TopMenu/TopMenu"
import React, { Component } from "react";
import "./styles.css"
import Select from 'react-select';
import axios from "axios";

class Intentions extends Component {
    constructor(props) {
        super(props);
        this.state = {
            intencionscribe: String,
            offering:Number,
            masslist: [],
            mass:String,
            erroreez:""
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    componentDidMount = async () => {
    
        var data =await(
                await fetch(this.props.store.backendadress+'/mass/availablemass')
        ).json();
        var list=[];
        data.forEach(element => {
            //console.log(element);
            let label="Msza z dnia  "+element.Date_Of_even.slice(0,10).split("-").reverse().join("-")+" godzina "+element.Date_Of_even.slice(11,16);
            list.push({value: element._id, label: label })
            
        });
        this.setState({masslist:list});
        

            //console.log(data);
           // console.log(response.body);
           // console.log(response.preview);
           // response.body.json()

       /* .then(json =>{
            console.log(json);
             //this.setState({anonselist: json})
        })
*/

    }
    handleChange = (name, value) => {
       // console.log("zmiana");
       // console.log(value);
       // console.log(name);

        this.setState({ [name]: value });
    };
    handleSubmit = async (e) => {
        var { intencionscribe,offering,mass}=this.state;
        //dopisac walidacje
        //alert("wyslij" + intencionscribe+offering+mass+ "   "+localStorage.getItem("id"));
        //console.log(this.props.store.backendadress);
        
       var config = {
            method: 'post',
            url: this.props.store.backendadress+'/mass/checkavailableaddinstans',
            headers: { 
              'Content-Type': 'application/json'
            },
          
            data : {
                "Paid_Off":false,
                "Textintens":intencionscribe,    
                "Mass":mass,
                "Ovner":localStorage.getItem("id"),
                "Amount":offering
          }
          };
          
          await axios(config)
         .then(async response =>{
            if(response.data.Intention&&response.data.Payment_id)
            {
              
            }else{
                
            }
        
         })
         
         
    }

    render() {



        const { masslist,erroreez} = this.state;

        return (



            <div>
                <TopMenu />
                <div className="kontent intecionkontent">

                    <div className="headerformintencion">Zgłoś Swoją intecję mszalną</div >
                    <label className="intencionformfield">
                        Proponowany termin odprawienia Mszy
                        <Select
                            className="basic-single widthselect"
                            classNamePrefix="select"
                            placeholder={"Wybierz"}
                            name="mass"
                            onChange={(e) => { this.handleChange("mass",e.value) }}
                            getOptionLabel={option => option.label}
                            getOptionValue={option => option.value}
                            options={masslist}
                        />

                    </label>
                    <label className="intencionformfield">
                        Twoja intencja
                        <input type="textarea"
                            name="intencionscribe"
                            className="intencionscribe"
                            placeholder="Wpisz treść Swojęj intencji"
                            pattern=""
                            onChange={(e) => { this.handleChange(e.target.name, e.target.value) }}
                            rows={5}
                            cols={5}
                        />

                    </label>

                    <label className="intencionformfield">
                        Zadeklaruj Ofiarę
                        <input  type="number"
                            name="offering"
                            className="offeringinput"
                           
                            pattern=""
                            onChange={(e) => { this.handleChange(e.target.name, e.target.value) }}
                            onKeyPress={(event) => {
                                if (!/[0-9]/.test(event.key)) {
                                  event.preventDefault();
                                }
                              }}
                        />

                    </label>

                    <button className="insidebutton" onClick={this.handleSubmit}>Zgłoś Swoją intecje i dokonaj ofiary</button>
                    {erroreez!==""?erroreez:""}

                </div>

            </div>
        )
    }
}



export default Intentions;
