import React, { Component } from "react";
import "./Myintencion.css"
import axios from "axios";
import { Link } from "react-router-dom";



class Myintencion extends Component {
    constructor(props) {
        super(props);
        this.state = {
          listintension:[],
          clienthaveintecion:false
        }
        this.handlepayforintencion = this.handlepayforintencion.bind(this);


    }
    handlepayforintencion = (id) => {
        console.log(id);
    }
    componentDidMount = () => {
        axios.get(this.props.store.backendadress + '/inten/myintecion', { params: { Ovner: localStorage.getItem("id") } })
            .then(res => {
                if(res.data[0]!==undefined){
                    let list=[];
                    console.log(res.data)
                    res.data.forEach(element => {
                        //console.log(element.massdate[0].Date_Of_even);
                        let date=element.massdate[0].Date_Of_even.slice(0, 10).split("-").reverse().join("-");
                        let time=element.massdate[0].Date_Of_even.slice(11, 16);
                        let pay="";
                        let akcepted="";
                        let button;

                        if(element.Accepted)
                        {
                            akcepted="Zakceptowana"
                        }else{
                            akcepted="Nie zaakceptowana"
                            
                        }
                        if(element.Paid_Off)
                        {
                            pay="Opłacona"
                        }else{
                            pay="Nie opłacona"
                            
                        }
                       
                        list.push(
                            <div key={element._id+"date"}> Data Mszy:{date}</div>,
                            <div key={element._id+"time"}>Godzina Mszy:{time}</div>,
                            <div key={element._id+"scribe"}>Treść twojej intencji:{element.Textintens}</div>,
                            <div key={element._id+"akcepted"}>Czy została zaakceptowana: {akcepted}</div>, 
                            <div key={element._id+"payment"}>Status płatności: {pay}</div>,
                            
                        )
                        if(!element.Paid_Off){
                            list.push(
                            <button className="exumationbutton" onClick={this.handlepayforintencion(element._id)} key={element._id+999}>Dokonaj ofiary za intencje</button>
                            )
                        }
                        this.setState({
                            listintension:list,
                            clienthaveintecion:true
                        })
                        
                    });
                }
        
            }
            )
    }
  

    render() {
        const{clienthaveintecion,listintension}=this.state;

        return (
            <div key={301}>
                {clienthaveintecion?[<div key={305}>{listintension}</div>]:[<div className="nullintecion" key={302}>
                    <div className="scribenullintecion" >Nie posiadasz żadnych intencji </div>
                    <Link to="/intencja" className="buttonorderinencion" key={304}>
                    Klikni by zamówić intencje mszalną
                    </Link></div>]}
            </div>
        )


    }

}

export default Myintencion
