import React,{Component} from "react";
import{Link} from "react-router-dom";
import "./TopMenu.css";
import Avatar from 'react-avatar';





class TopMenu extends Component{
    state={
        islogin:Boolean,
    }
    componentDidMount= () =>{
        let log= localStorage.getItem("token");
        console.log(log)
        if(log !==null)
        {
            this.setState({islogin:true})
        }else{
            this.setState({islogin:false})
        }
        
    }
    handleLogout = () => {
        localStorage.removeItem("token")
        
        window.location.reload()
        }
      handleLogin = () => {
            
            window.location = "/login"
            }
    
 
 
    render(){
        const{islogin}=this.state
 
        return(
            <div className="topmenuconteiner">
                <div className="usersection">
                <Avatar color={Avatar.getRandomColor('sitebase', ['red', 'green', 'blue'])} name="Wim Mostmans" size={60} round="20px"/>
                {islogin?
                        [<button className="topmenubuttonlog" onClick={this.handleLogin}>Wyloguj się </button>]
                        :[<button className="topmenubuttonlog" onClick={this.handleLogout}>Zaloguj się </button>]}
                </div>
            <div className="topmenurip">
                <Link to="/login">
                    <div className="topmenubutton" >
                        Cmentarz
                    </div>
                </Link>
                <Link to="/anonse">
                    <div className="topmenubutton" >
                        Ogłoszenia Parafialne
                    </div>
                </Link>
                <Link to="/login">
                    <div className="topmenubutton" >
                        Kontakt
                    </div>
                </Link>
                
               
              
            </div>
         </div>
        )
    }
}
export default TopMenu
