import React, { Component } from "react";
import "./Payment.css";
import TopMenu from "../TopMenu/TopMenu";
import Loaderspiner from "../Loaderspiner/Loaderspiner";

class Payment extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isprocecing: false,
      wronglink: false,
      statuspayment: false,


    };


  }

  componentDidMount = async () => {
    //console.log(this.props.store.backendadress);
    // console.log(window.location.search.split("=")[1]);
    var tid=window.location.search.split("=")[1];
    if (tid === undefined) {
      this.setState({
        wronglink: true,
        isprocecing: true
      });
    }else{
      await fetch(this.props.store.backendadress + '/payment/checkpaymenbytid?'+ new URLSearchParams({
       tid: tid
    })).then(response=>{
      console.log(response);
    })
    }
    //  await fetch(this.props.store.backendadress + '/mass/availablemass')




  }










  render() {
    const { isprocecing, statuspayment, statuspaymentdiv, wronglink } = this.state;
    return (
      <div key={"paymentdiv"}>
        <TopMenu></TopMenu>
        <div className="kontentpayment" key="kontentpayment">
          <div className="kontentpaymenttitle" key="kontentpaymenttitle">Status twojej płatności</div>
          {!isprocecing ? [

            <div className="paymentprocesing" key="paymentprocesing">
              <Loaderspiner />
              Oczekujemy na potwierdzenie płatności od dostawcy płatności

            </div>

          ] : [
            <div >Twoja płatność:
            </div>

          ]}
          {wronglink ? <div className="paymentwronglink" key="paymentwronglink">Niestety adres przekierowania jest błedny sprawdz status swojej płatności w zakładce moje konto</div> : ""}
          {statuspayment ? statuspaymentdiv : ""}


        </div>

      </div>


    )
  }
}
export default Payment
