import TopMenu from "../TopMenu/TopMenu"
import React, { Component } from "react";
import "./styles.css"
import Select from 'react-select';

class Intentions extends Component {
    constructor(props) {
        super(props);
        this.state = {
            intencionscribe: String,
            intentionprice: Number,
            masslist: [],
            mass:String
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    componentDidMount = async () => {
        var data =await(
                await fetch(this.props.store.backendadress+'/mass/availablemass')
        ).json();
        var list=[];
        data.forEach(element => {
            let label=element.Date_Of_even.toISOString().slice(0,10).split("-").reverse().join("-")+" godzina "+element.Date_Of_even.toISOString().slice(11,16);
            list.push({value: element.id, label: label })
            
        });
            console.log(data);
           // console.log(response.body);
           // console.log(response.preview);
           // response.body.json()

       /* .then(json =>{
            console.log(json);
             //this.setState({anonselist: json})
        })
*/

    }
    handleChange = (name, value) => {
        console.log("zmiana");
        console.log(value);
        console.log(name);

        this.setState({ [name]: value });
    };
    handleSubmit = async (e) => {
        alert("wyslij");
    }

    render() {



        const { intentionname, intentionprice } = this.state;

        return (



            <div>
                <TopMenu />
                <div className="kontent intecionkontent">

                    <div className="headerformintencion">Zgłoś Swoją intecję mszalną</div >
                    <label className="intencionformfield">
                        Proponowany termin odprawienia Mszy
                        <Select
                            className="basic-single"
                            classNamePrefix="select"
                            placeholder={"Wybierz"}
                            name="mass"
                            options={this.masslist}
                        />

                    </label>
                    <label className="intencionformfield">
                        Twoja intencja
                        <input type="textarea"
                            name="intencionscribe"
                            className="intencionscribe"
                            placeholder="Wpisz treść Swojęj intencji"
                            pattern=""
                            onChange={(e) => { this.handleChange(e.target.name, e.target.value) }}
                            rows={5}
                            cols={5}
                        />

                    </label>

                    <button className="insidebutton" onClick={this.handleSubmit}>Zamów</button>

                </div>

            </div>
        )
    }
}



export default Intentions;
