import React, { Component } from "react";
import "./Mydata.css"
import axios from "axios";
import Loaderspiner from "../../Loaderspiner/Loaderspiner";



class Mydata extends Component {
    constructor(props) {
        super(props);

        this.state = {
            useremail: localStorage.getItem("emailrip_app"),
            userpassword: "",
            userstatus: false,
            changedata: false,
            Namefalse: false,
            LastNamefalse: false,
            Peselfalse: false,
            HomeNumberfalse: false,
            HometwoNumberfalse: false,
            Streetfalse: false,
            cityfalse: false,
            userpasswordfalse: false,
            notyficacion: false,
            errorvalidate: false,
            sendingrequest: false,
            sendrequest: false,
            fieldfalse:"",
            ovnerrip: {
                HomeNumber: 0,
                HometwoNumber: 0,
                LastName: "",
                Name: "",
                Pesel: 0,
                Street: "",
                city: "",
                email: "",
                ovner: "",
                _id: ""
            },
        }



    }

    componentDidMount = () => {

        axios.get(this.props.store.backendadress + '/userdb/userdata', { params: { id: localStorage.getItem('id') } })
            .then(res => {
                //   console.log(res.data);
                const email = res.data.email;
                const statususer = res.data.status;
                //console.log(statususer);
                this.setState({
                    useremail: email,
                    userpassword: res.data.password,
                    userstatus: statususer
                });

                if (statususer) {

                    //console.log("pobracovnerripa"+email)
                    axios.get(this.props.store.backendadress + '/ovnerrip/getdataovnerrip', { params: { email: email } })
                        .then(res => {
                            // console.log(res.data);

                            let ovner = res.data[0];
                            // console.log(ovner);
                            this.setState({ ovnerrip: ovner });

                        })


                }
            })
    }
    savedate = async () => {
        //alert("is work");
        const{ovnerrip,userpassword}=this.state;
        
        var fieldfalsetmp=[];
        var name=[{key:"HomeNumber",
                    string:"Numer Domu",
                    value:ovnerrip.HomeNumber},
                    {key:"HometwoNumber",
                    string:"Numer Mieszkania",
                    value:ovnerrip.HometwoNumber},
                    {key:"Name",
                    string:"Imię",
                    value:ovnerrip.Name},
                    {key:"LastName",
                    string:"Nazwisko",
                    value:ovnerrip.LastName},
                    {key:"Pesel",
                    string:"Pesel",
                    value:ovnerrip.Pesel},
                    {key:"Street",
                    string:"Ulica",
                    value:ovnerrip.Street},
                    {key:"city",
                    string:"Miasto",
                    value:ovnerrip.city},
                    {key:"userpassword",
                    string:"Hasło",
                    value:userpassword}]
        //list.push({ value: element._id, label: label })
        /*HomeNumber:HometwoNumber:LastName:Name:Pesel Street:city: email: ovner: _id: */
        name.forEach(async element=>{
