import React, { Component } from "react";
import "./Mydata.css"
import axios from "axios";
import { Link } from "react-router-dom";





class Mydata extends Component {
    constructor(props) {
        super(props);
        this.state = {
            useremail: "",
            userpassword: "",
            userstatus: false,
            
            ovnerrip: {
                Number: 0,
                HometwoNumber: 0,
                LastName: "",
                Name: "",
                Pesel: 0,
                Street: "",
                city: "",
                email: "",
                ovner: "",
                _id: ""
            },
        }
   


    }

    componentDidMount = () => {
        axios.get(this.props.store.backendadress + '/userdb/userdata', { params: { id: localStorage.getItem('id') } })
        .then(res => {
            //   console.log(res.data);
            const email = res.data.email;
            const statususer = res.data.status;
            //console.log(statususer);
            this.setState({
                useremail: email,
                userpassword: res.data.password,
                userstatus: statususer
            });

            if (statususer) {

                //console.log("pobracovnerripa"+email)
                axios.get(this.props.store.backendadress + '/ovnerrip/getdataovnerrip', { params: { email: email } })
                    .then(res => {
                        // console.log(res.data);

                        let ovner = res.data[0];
                        // console.log(ovner);
                        this.setState({ ovnerrip: ovner });

                    })

      
            }
        })
    }
    

    render() {
        const { useremail, userpassword, ovnerrip, userstatus, showgrave, showmydata, showmyintencion} = this.state;
        const {store}=this.props;

        return (
            <div className="userdatafield" key={178}>
            <div className="userdatacolumn" key={78}>
                <label key={781}>Email</label>
                <input key={782} className="inputdata" disabled="disabled" value={useremail} onChange={(e) => { this.handleChange(e.target.name, e.target.value) }} />
                {userstatus ? [
                    <label key={783}>Imię</label>,
                    <input key={784} className="inputdata" value={ovnerrip.Name || ''} onChange={(e) => { this.handleChange(e.target.name, e.target.value) }} />,
                    <label key={785}>Nazwisko</label>,
                    <input key={786} className="inputdata" value={ovnerrip.LastName || ''} onChange={(e) => { this.handleChange(e.target.name, e.target.value) }} />,
                    <label key={787} >Pesel</label>,
                    <input key={788} className="inputdata" disabled="disabled" value={ovnerrip.Pesel || ''} onChange={(e) => { this.handleChange(e.target.name, e.target.value) }} />,
                ] : ['']}

            </div>
            <div className="userdatacolumn" key={79}>
                <label key={791}>Hasło</label>
                <input key={792} className="inputdata" value={userpassword || ''} onChange={(e) => { this.handleChange(e.target.name, e.target.value) }} />
                {userstatus ? [
                    <label key={793}>Ulica</label>,
                    <input key={794} className="inputdata" value={ovnerrip.Street || ''} onChange={(e) => { this.handleChange(e.target.name, e.target.value) }} />,
                    <label key={795}>Numer domu</label>,
                    <input key={796} className="inputdata" value={ovnerrip.HomeNumber || ''} onChange={(e) => { this.handleChange(e.target.name, e.target.value) }} />,
                    <label key={797}>Numer mieszkania</label>,
                    <input key={798} className="inputdata" value={ovnerrip.HometwoNumber || ''} onChange={(e) => { this.handleChange(e.target.name, e.target.value) }} />,
                    <label key={799}>Miasto</label>,
                    <input key={7910} className="inputdata" value={ovnerrip.city || ''} onChange={(e) => { this.handleChange(e.target.name, e.target.value) }} />,

                ] : [""]}

            </div>





        </div>
        )


    }

}

export default Mydata
