import React, { Component } from "react";
import TopMenu from "../TopMenu/TopMenu";
import Myquater from "../Myquater/Myquater";
import Myintencion from"../Myintencion/Myintencion";
import "./Myaccount.css"
import axios from 'axios';



class Myaccount extends Component {
    constructor(props) {
        super(props);
        this.state = {
            useremail: "",
            userpassword: "",
            userstatus: false,
            
            ovnerrip: {
                Number: 0,
                HometwoNumber: 0,
                LastName: "",
                Name: "",
                Pesel: 0,
                Street: "",
                city: "",
                email: "",
                ovner: "",
                _id: ""
            },
            showgrave: false,
            showmydata: true,
            showmyintencion: false

        }
        this.handleChange = this.handleChange.bind(this);
        this.handleshowgrave = this.handleshowgrave.bind(this);
        this.handlemydata = this.handlemydata.bind(this);
        this.handlemyintencion = this.handlemyintencion.bind(this);



    }

    componentDidMount = () => {
        var { listquaters } = this.state;
        axios.get(this.props.store.backendadress + '/userdb/userdata', { params: { id: localStorage.getItem('id') } })
            .then(res => {
                //   console.log(res.data);
                const email = res.data.email;
                const statususer = res.data.status;
                //console.log(statususer);
                this.setState({
                    useremail: email,
                    userpassword: res.data.password,
                    userstatus: statususer
                });

                if (statususer) {

                    //console.log("pobracovnerripa"+email)
                    axios.get(this.props.store.backendadress + '/ovnerrip/getdataovnerrip', { params: { email: email } })
                        .then(res => {
                            // console.log(res.data);

                            let ovner = res.data[0];
                            // console.log(ovner);
                            this.setState({ ovnerrip: ovner });

                        })

          
                }
            })



    }
    handleshowgrave = () => {



        this.setState({
            showgrave: true,
            showmydata: false,
            showmyintencion: false
        });
    }
    handlemydata = () => {

        this.setState({
            showgrave: false,
            showmydata: true,
            showmyintencion: false
        });
    }
    handlemyintencion = () => {

        this.setState({
            showgrave: false,
            showmydata: false,
            showmyintencion: true
        });
    }
    handleChange = (name, value) => {

        // console.log("zmiana");
        // console.log(value);
        // console.log(name);

        this.setState({ [name]: value });
    }

    render() {

        const { useremail, userpassword, ovnerrip, userstatus, showgrave, showmydata, showmyintencion,statususer } = this.state;
        const {store}=this.props;



        // console.log(listburial);

        // const{listgrave}=this.state;

        // console.log(listgrave);

        // const{listexumation}=this.state;

        // console.log(listexumation);

        /*
                if(listgrave[0]!==undefined){
                    listgrave.forEach((gravequarters) => {
                        console.log(gravequarters);
                        console.log("aaa");
                        let gravedata=gravequarters.time_event.toString().slice(0,10).split("-").reverse().join("-");
                        console.log(gravedata);
                        list.push(<div className="listgrave">
                            <div className="anonsetitle">
                                {gravequarters.IdGraveQuaters}
                            </div>
                            <div className="anonsedate">
                                {gravequarters.DatePayment}
                            </div>
                            <div className="anonsscribe">
                                {gravequarters.Payment}
                            </div>
                            <div className="anonsscribe">
                                {gravequarters.ovnerripid}
                            </div>
                            <div className="anonsscribe">
                                {gravequarters.NumberenableTraditionalBurials}
                            </div>
                            <div className="anonsscribe">
                                {gravequarters.NumberenableUrnBurials}
                            </div>
                            <div className="anonsscribe">
                                {gravequarters.NumberTraditionalBurials}
                            </div>
                            <div className="anonsscribe">
                                {gravequarters.NumberUrnBurials}
                            </div>
                            <div className="anonsscribe">
                                {gravequarters.MethodOfPayment}
                            </div>
                            <div className="anonsscribe">
                                {gravequarters.TypeOF}
                            </div>
                        </div>
                    );
                    });
                }*/


        return (
            <div>
                <TopMenu key={76} />
                <div className="userdata" key={77}>
                    <div key={101} className="menuuser">
                        <button key={102} className="menuuserbutton" onClick={this.handleshowgrave}>Pokaż moje kwatery</button>
                        <button key={103} className="menuuserbutton" onClick={this.handlemydata}>Pokaż moje dane</button>
                        <button key={104} className="menuuserbutton" onClick={this.handlemyintencion}>Pokaż moje Intencje</button>

                    </div>
                    {showmydata ? [<div className="userdatafield" key={178}>
                        <div className="userdatacolumn" key={78}>
                            <label key={781}>Email</label>
                            <input key={782} className="inputdata" disabled="disabled" value={useremail} onChange={(e) => { this.handleChange(e.target.name, e.target.value) }} />
                            {userstatus ? [
                                <label key={783}>Imię</label>,
                                <input key={784} className="inputdata" value={ovnerrip.Name || ''} onChange={(e) => { this.handleChange(e.target.name, e.target.value) }} />,
                                <label key={785}>Nazwisko</label>,
                                <input key={786} className="inputdata" value={ovnerrip.LastName || ''} onChange={(e) => { this.handleChange(e.target.name, e.target.value) }} />,
                                <label key={787} >Pesel</label>,
                                <input key={788} className="inputdata" disabled="disabled" value={ovnerrip.Pesel || ''} onChange={(e) => { this.handleChange(e.target.name, e.target.value) }} />,
                            ] : ['']}

                        </div>
                        <div className="userdatacolumn" key={79}>
                            <label key={791}>Hasło</label>
                            <input key={792} className="inputdata" value={userpassword || ''} onChange={(e) => { this.handleChange(e.target.name, e.target.value) }} />
                            {userstatus ? [
                                <label key={793}>Ulica</label>,
                                <input key={794} className="inputdata" value={ovnerrip.Street || ''} onChange={(e) => { this.handleChange(e.target.name, e.target.value) }} />,
                                <label key={795}>Numer domu</label>,
                                <input key={796} className="inputdata" value={ovnerrip.HomeNumber || ''} onChange={(e) => { this.handleChange(e.target.name, e.target.value) }} />,
                                <label key={797}>Numer mieszkania</label>,
                                <input key={798} className="inputdata" value={ovnerrip.HometwoNumber || ''} onChange={(e) => { this.handleChange(e.target.name, e.target.value) }} />,
                                <label key={799}>Miasto</label>,
                                <input key={7910} className="inputdata" value={ovnerrip.city || ''} onChange={(e) => { this.handleChange(e.target.name, e.target.value) }} />,

                            ] : [""]}

                        </div>





                    </div>] : []}
                    {(showgrave&&userstatus)?[<Myquater key={200} store={store} email={useremail}/>]:[]}
                    {(showmyintencion)?[<Myintencion key={200} store={store} email={useremail}/>]:[]}
                </div>

            </div>
        )
    }
}
export default Myaccount
