import{Link} from "react-router-dom";
import "./TopMenu.css"




class TopMenu extends Component{
    state={
        islogin:Boolean,
    }
    componentDidMount= () =>{
        
        
    }
    handleLogout = () => {
        localStorage.removeItem("token")
        
        window.location.reload()
        }
      handleLogin = () => {
            
            window.location = "/login"
            }
    
 
 
    render(){
        const{islogin}=this.state
 
        return(
            <div className="topmenuconteiner">
                <div className="usersection">

                {islogin?
                        [<button className="topmenubutton" onClick={this.handleLogin}>Wyloguj się </button>]
                        :[<button className="topmenubutton" onClick={this.handleLogout}>Zaloguj się </button>]}
                </div>
            <div className="topmenurip">
                <Link to="/login">
                    <div className="topmenubutton" >
                        Cmentarz
                    </div>
                </Link>
                <Link to="/anonse">
                    <div className="topmenubutton" >
                        Ogłoszenia Parafialne
                    </div>
                </Link>
                <Link to="/login">
                    <div className="topmenubutton" >
                        Kontakt
                    </div>
                </Link>
                
               
              
            </div>
         </div>
        )
    }
}
export default TopMenu
