import { string } from "prop-types";
import serverurl from "../../reduser/store"
const backendadress="sa";


class Myaccount extends Component{
    constructor(props) {
        super(props);
        this.state = {
        useremail: string,
        userpassword:string,
        userstatus:Boolean,
        ovnerripname:string,
        ovnerriplastname:string,
        ovnerripadress:string,
        ovnerriphousenr:string,
        ovnerripapartmentnr:string,
        ovnerripcity:string,
        ovnerrippesel:string,
        listburial: JSON,
        listexumation: JSON,
        listgrave: JSON
    }
    this.handleChange = this.handleChange.bind(this);

    }

    componentDidMount= () =>{
      
        axios.get(this.props.store.backendadress+'/userdb/userdata',{params:{id:localStorage.getItem('id')}} )
        .then(res => {
         //   console.log(res.data);
         const email=res.data.email;
         const statususer=res.data.status;
         //console.log(statususer);
         this.setState({useremail: email,
                        userpassword: res.data.password,
                        userstatus:statususer});
       
                if(statususer)
                {
               
                //console.log("pobracovnerripa"+email)
                axios.get(this.props.store.backendadress+'/ovnerrip/getdataovnerrip',{params:{email:email}} )
                    .then(res => {
                     // console.log(res.data);
                    //  console.log(res.data[0]);
                        let ovner=res.data[0];
                    this.setState({ ovnerriplastname:ovner.LastName,
                                    ovnerripname:ovner.Name,
