import TopMenu from "../TopMenu/TopMenu"
import React, { Component } from "react";
import "./Intentions.css"
import Select from 'react-select';
import axios from "axios";

class Intentions extends Component {
    constructor(props) {
        super(props);
        this.state = {
            intencionscribe: String,
            offering: Number,
            masslist: [],
            mass: String,
            massscribe:String,
            erroreez: "",
            intencionrez: false
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleEdit = this.handleEdit.bind(this);
        this.handleCancel = this.handleCancel.bind(this);
    }
    componentDidMount = async () => {
        // sprawdzic czy zostala rospoczeta rezerwacja intencji 
        var check = localStorage.getItem("Intention");
        console.log(check);
        if (check == null) {
            var data = await (
                await fetch(this.props.store.backendadress + '/mass/availablemass')
            ).json();
            var list = [];
            data.forEach(element => {
                //console.log(element);
                let label = "Msza z dnia  " + element.Date_Of_even.slice(0, 10).split("-").reverse().join("-") + " godzina " + element.Date_Of_even.slice(11, 16);
                list.push({ value: element._id, label: label })

            });
            this.setState({ masslist: list });

        } else {
            // sprawdzic czy oplacona i dopiero przekierowac
            //window.location = "/paymentsummary"
            
            
            var Payment_id = localStorage.getItem("Payment_id");
            var amount = localStorage.getItem("Amount");
            var mass = localStorage.getItem("Mass");
            var massscribe = localStorage.getItem("MassScribe").replace("Msza", "mszę").replace("godzina", "godzinę");
            var textintens = localStorage.getItem("Textintens");
            if ( Payment_id != null && amount != null && mass != null && massscribe != null && textintens != null) {
                this.setState({
                    intencion: check,
                    Payment_id: Payment_id,
                    amount: amount,
                    mass: mass,
                    massscribe: massscribe,
                    intencionscribe: textintens,
                    intencionrez:true
                })
            }
        }


    }
    handleChange = (name, value) => {
        var {masslist}=this.state;
        // console.log("zmiana");
        // console.log(value);
        // console.log(name);

        this.setState({ [name]: value });
        if(name==="mass"){
            //console.log(value);
           // console.log(masslist);
            for(let i=0;i<masslist.length;i++){
                if(masslist[i].value===value)
                {  // console.log("in"+masslist[i]);
                    this.setState({massscribe:masslist[i].label});
                    i=masslist.length;
                }
            }
        }
    };
    handleEdit = async () => {
        alert("progress implementacio");
    }
    handleCancel = async () => { alert("progress implementacio"); }
    
    handleSubmit = async (e) => {
        var { intencionscribe, offering, mass,massscribe } = this.state;
        //dopisac walidacje
        //alert("wyslij" + intencionscribe+offering+mass+ "   "+localStorage.getItem("id"));
        //console.log(this.props.store.backendadress);

        var config = {
            method: 'post',
            url: this.props.store.backendadress + '/mass/checkavailableaddinstans',
            headers: {
                'Content-Type': 'application/json'
            },

            data: {
                "Paid_Off": false,
                "Textintens": intencionscribe,
                "Mass": mass,
                "Ovner": localStorage.getItem("id"),
                "Amount": offering
            }
        };

        await axios(config)
            .then(async response => {
                if (response.data.Intention && response.data.Payment_id) {
                    //zarezerwowana intencja
                    localStorage.setItem("Payment_id", response.data.Payment_id);
                    localStorage.setItem("Intention", response.data.Intention);
                    localStorage.setItem("Amount", offering);
                    localStorage.setItem("Mass", mass);
                    localStorage.setItem("MassScribe", massscribe);
                    localStorage.setItem("Textintens", intencionscribe);

                    window.location = "/paymentsummary"
                    

                } else {

                }

            })


    }

    render() {



        const { masslist, erroreez, intencionrez  ,intencionscribe,offering,massscribe} = this.state;
        console.log(typeof offering);
        console.log(offering);
        var offeringplaceholder=toString(offering);
        console.log(typeof offeringplaceholder);
        return (



            <div>
                <TopMenu />
                <div className="kontent intecionkontent" >


                    <div className="headerformintencion">Zgłoś Swoją intecję mszalną</div>
                    <label className="intencionformfield">
                        Proponowany termin odprawienia Mszy
                        <Select
                            className="basic-single widthselect"
                            classNamePrefix="select"
                            placeholder={intencionrez?massscribe:"Wybierz"}
                            name="mass"
                            onChange={(e) => { this.handleChange("mass", e.value) }}
                            getOptionLabel={option => option.label}
                            getOptionValue={option => option.value}
                            options={masslist}
                        />

                    </label>
                    <label className="intencionformfield">
                        Twoja intencja
                        <input type="textarea"
                            name="intencionscribe"
                            className="intencionscribe"
                            placeholder={intencionrez?intencionscribe:"Wpisz treść Swojęj intencji"}
                            pattern=""
                            onChange={(e) => { this.handleChange(e.target.name, e.target.value) }}
                            rows={5}
                            cols={5}
                        />

                    </label>

                    <label className="intencionformfield">
                        Zadeklaruj Ofiarę
                        <input type="number"
                            name="offering"
                            className="offeringinput"
                            placeholder={intencionrez?offeringplaceholder:"100"}
                            pattern=""
                            onChange={(e) => { this.handleChange(e.target.name, e.target.value) }}
                            onKeyPress={(event) => {
                                if (!/[0-9]/.test(event.key)) {
                                    event.preventDefault();
                                }
                            }}
                        />

                    </label>
                    {(!intencionrez) ? [
                    <> <button className="intencionbutton" onClick={this.handleSubmit}>Zgłoś swoją intencję i dokonaj ofiary</button>
                        {erroreez !== "" ? <div className="errormesage">erroreez</div> : ""}</>


                    ] : [
                        <>
                            <button className="intencionbutton " onClick={this.handleEdit}> Edytuj swoją intencję i dokonaj ofiary</button>
                            <button className="intencionbutton " onClick={this.handleCancel}>Chce anulować zgłaszanie intencji </button>
                        </>]}


                </div>

            </div>
        )
    }
}



export default Intentions;
