        const url = serverurl.backendadress+"/auth"
        const { data: res } = await axios.post(url, data)
        localStorage.setItem("token", res.data.token)
        localStorage.setItem("id", res.data.id)
        console.log(res.data);
        window.location = "/"
        
    } catch (error) {
    if (
    error.response &&
