import React, { Component } from "react";
import "./Myintencion.css"
import axios from "axios";
import { Link } from "react-router-dom";



class Myintencion extends Component {
    constructor(props) {
        super(props);
        this.state = {
          listintension:[],
          clienthaveintecion:false
        }



    }
    componentDidMount = () => {
        axios.get(this.props.store.backendadress + '/inten/myintecion', { params: { Ovner: localStorage.getItem("id") } })
            .then(res => {
                if(res.data[0]!==undefined){
                    let list=[];
                    //console.log(res.data)
                    res.data.forEach(element => {
                        //console.log(element.massdate[0].Date_Of_even);
                        let date=element.massdate[0].Date_Of_even.slice(0, 10).split("-").reverse().join("-");
                        let time=element.massdate[0].Date_Of_even.slice(11, 16);
                        let pay="";
                        if(element.Paid_Off)
                        {
                            pay="Nie Oplacona"
                        }else{
                            pay="Nie Oplacona"
                        }
                       
                        list.push(
                            <div key={element._id+"date"}> Data Mszy {date}</div>,
                            <div key={element._id+"time"}>Godzina Mszy{time}</div>,
                            <div key={element._id+"scribe"}>Treść twojej intencji{element.Textintens}</div>,
                            <div key={element._id+"payment"}>Godzina Mszy{element.Textintens}</div>,
                        )
                        this.setState({
                            listintension:list,
                            clienthaveintecion:true
                        })
                        
                    });
                }
        
            }
            )
    }


    render() {
        const{clienthaveintecion,listintension}=this.state;

        return (
            <div key={301}>
                {clienthaveintecion?[listintension]:[<div className="nullintecion" key={302}>
                    <div className="scribenullintecion" >Nie posiadasz żadnych intencji </div>
                    <Link to="/intencja" className="buttonorderinencion" key={304}>
                    Klikni by zamówić intencje mszalną
                    </Link></div>]}
            </div>
        )


    }

}

export default Myintencion
