import React, { Component } from "react";
import axios from "axios";
import { Link} from "react-router-dom";
//import {Alert } from "react";
import styles from "./styles.module.css";



class Signup extends Component {
    constructor(props) {
        super(props);
        
        this.state = {
            email: "",
            password: "",
            error: ""
        }
    
    this.handleChange= this.handleChange.bind(this);
    this.handleSubmit= this.handleSubmit.bind(this);
}
handleChange = (name,value) => {
       this.setState({[name]: value });
  };
  validate=(email,password)=>{
    //dopisać walidacje
    if(email!==""&&password!=="")
    {
        return true;
    }
    else{
        return false;
    }
    
  }

handleSubmit = async (e) => {
   const{email,password}=this.state;
   if(this.validate(email,password))
   {
    try {
        const {email,password} = this.state
        await axios.post(this.props.store.backendadress+"/user/registeruser", {email:email,password:password})
        .then(res=>{
            console.log("k"+res);
            if(res){
                if(res.status=200){
                   // window.location = "/login";
                }else alert("Coś poszlo nie tak zprobuj za chwile")
    
            }

        })
        
        


        
    } catch (error) {
            if (
            error.response &&
            error.response.status >= 400 &&
            error.response.status <= 500
            ) {
            console.log(error.response.data.message);
            }
    }
   }else{
    this.setState({error:"Dane rejestracji są niepoprawne"});
   }
    
}



render(){
    const{email,password,error}=this.state;
    return (
        <div className={styles.signup_container}>
            <div className={styles.signup_form_container}>
                <div className={styles.left}>
                    <h1>Witaj </h1>
                    <h1>z powrotem</h1>
                    <Link to="/registeruser">
                        <button type="button"
                            className={styles.white_btn}>
                            Zaloguj się
                        </button>
                    </Link>
                </div>
            <div className={styles.right}>
                <div className={styles.form_container}
                    >
                    <h1>Zakładanie konta</h1>
                
                    <input
                        type="email"
                        placeholder="Email"
                        name="email"
                        onChange={(e) => {this.handleChange(e.target.name,e.target.value)}}
                        value={email}
                        required
                        className={styles.input}
                    />
                    <input
                        type="text"
                        placeholder="Hasło"
                        name="password"
                        onChange={(e) => {this.handleChange(e.target.name,e.target.value)}}
                        value={password}
                        required
                        className={styles.input}
                    />
                    {error && <div
                        className={styles.error_msg}>{error}</div>}
                    <button type="submit"
                        className={styles.green_btn}
                            onClick={this.handleSubmit}>
                        Zarejestruj się
                    </button>
                </div>
            </div>
            </div>
        </div>
        );
}

}

    export default  Signup;
